mapper.select:
SELECT t.yunfei,(t.order_money*t.order_number) as
zongjia,t.order_product_id,t.order_number,t.order_money,t.Order_no,order_mode,order_id,order_product_id,order_state,order_shouhuo_id
from t_order t where 1=1 and  t.user_id=#{user_id}

and  t.order_id=#{id}   


	and t.order_state=#{order_state}

order by order_time desc

SELECT freight FROM t_freight
WHERE
t_freight.price>#{yunfei} and t_freight.prices <=#{yunfei}

mapper.update:
UPDATE t_shopping
SET t_shopping.is_dianpng = 2
WHERE
t_shopping.order_id=#{order_id}
AND t_shopping.product_id = #{product_id}

UPDATE  t_order
SET order_shouhuo_id=#{order_shouhuo_id}
WHERE  order_id=#{order_id}

mapper.select:
	select  t_discount.id,t_discount.price  from  t_discount join   t_discount_product  
    ON   t_discount_product.discount_id=t_discount.id join   t_product   on t_product.id=t_discount_product.product_id
    where  t_product.id=#{id}

SELECT t.yunfei,t.order_money as
zongjia,t.order_product_id,t.order_number,t.order_money,t.Order_no,order_mode,order_id,order_shouhuo_id,order_state
from t_order t where 1=1 or Order_no=#{date}


	and t.order_state=#{order_state}

mapper.update:
UPDATE  t_order
SET order_payment=#{order_payment}
WHERE  order_id=#{order_id}
