mapper.select:
SELECT COUNT(*) FROM ( SELECT p.id FROM t_posting p,t_comment c,t_plate pl WHERE p.id=c.posting_id and p.plate_id=pl.id GROUP BY c.posting_id
limit #{page},#{row}
) a

select p.id, p.title, p.create_time, pl.id pl_id, pl.plate_name, (select COUNT(*) from t_comment where posting_id=p.id) commentCount from t_posting p, t_plate pl where p.plate_id = pl.id
and p.plate_id = #{plate_id}
ORDER BY create_time DESC
limit #{row,javaType=INTEGER}

    	select p.id, p.title, p.is_good ,p.create_time, pl.id pl_id, pl.plate_name 
    	from t_posting p, t_plate pl
		where p.plate_id = pl.id and p.is_good = #{isGood,javaType=INTEGER}
    	ORDER BY p.toGood_time DESC

    		limit #{row,javaType=INTEGER}

SELECT COUNT(DISTINCT tb.id) count FROM ( select po.id id from t_posting po
po.user_id =#{user_id}
)as tb
